import { Component } from '@angular/core';
import { Observable } from 'rxjs';

import { CounterState } from '../../reducers/counter.reducer';
import { CounterService } from '../../services/counter.service';

@Component({
  selector: 'app-service-counter',
  templateUrl: './service-counter.component.html',
  styleUrls: ['./service-counter.component.css'],
})
export class ServiceCounterComponent {
  public count$: Observable<CounterState>;

  constructor(private counterService: CounterService) {
    this.count$ = this.counterService.getCount();
  }

  public increment(): void {
    this.counterService.increment();
  }

  public decrement(): void {
    this.counterService.decrement();
  }

  public reset(newCount: string): void {
    const count = parseInt(newCount, 10);
    if (!Number.isNaN(count)) {
      this.counterService.reset(count);
    }
  }
}
<h1>Counter connected to the service</h1>

<p>
  <strong data-testid="count">{{ count$ | async }}</strong>
</p>

<p>
  <button (click)="increment()" data-testid="increment-button">+</button>
  <button (click)="decrement()" data-testid="decrement-button">-</button>
</p>

<p>
  <input type="number" #input data-testid="reset-input" />
  <button (click)="reset(input.value)" data-testid="reset-button">Reset</button>
</p>
